#widget {
  width: 100%;
  height: 100%;
  font-size: 10px;
  font-family: 'Noto Sans KR';
}

/*
  Widget Header Area
*/

header.widget-header {
  width: 100%;
  padding: 2em;

  border-bottom: 1px solid rgba(0, 0, 0, 0.1);
}

/* widget-header-right-section */
.widget-header-right-section .widget-header-action {
  display: block;
  font-size: 1.8em;
  margin-left: 0.5em;
  color: black;
  cursor: pointer;
}

/*
  Widget Content Area
*/

main.widget-content {
  width: 100%;
}

.cell-icon {
  border: 1px solid #d7dbec;
  border-radius: 0.5em;
  padding: 0.2em;
  margin-right: 1.5em;
  cursor: pointer;
  width: 1em;
  height: 1em;
  font-size: 1.8em;
}

main.widget-content .table {
  width: 100%;
  height: 100%;
  overflow-y: auto;
}

main.widget-content .table table {
  width: 100%;
  height: 100%;
  border-spacing: 0;
  /* tr에 border styling 하기 위해 필요 */
  border-collapse: separate;

  table-layout: fixed;
}

main.widget-content .table table thead {
  height: 20%;
}
main.widget-content .table table tbody {
  height: 80%;
}

main.widget-content .table table tbody tr {
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);
}

main.widget-content .table table thead tr {
  position: sticky;
  top: -1px;
  cursor: pointer;
  background-color: #fafafc;
}
main.widget-content .table table th {
  text-align: start;
}

main.widget-content .table table thead tr th {
  color: rgba(0, 0, 0, 0.4);
  padding: 0 calc(2em / 1.2);
  font-size: 1.2em;

  border-bottom: 1px solid #e6e9f4;

  max-width: calc(10em / 1.2);
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

main.widget-content .table table tbody tr:nth-child(odd) {
  background-color: white;
}

main.widget-content .table table tbody tr:nth-child(even) {
  background-color: #fafafc;
}

main.widget-content .table table tbody tr:nth-child(odd):hover {
  background-color: #efefef;
}

main.widget-content .table table tbody tr:nth-child(even):hover {
  background-color: #efefef;
}

main.widget-content .table table tbody tr td {
  color: rgba(0, 0, 0, 0.9);
  padding: 0 calc(2em / 1.4);
  font-size: 1.4em;
}

main.widget-content .table table .tail {
  border-top: solid 1px #efefef;
}

.arrow-btn,
.pageList div,
.selector,
.rowLimit2 div,
.action-btn {
  cursor: pointer;
}

descriptor.controllerScript:
self.onInit = function () {
  // 변수 정의
  defineVariables();

  // 위젯 타이틀 설정
  setTitle();

  // 이벤트 설정
  applyEvent();
};

self.onDataUpdated = function () {
  parseData();
  sortData();
  makeTable();

  self.ctx.detectChanges();
  self.onResize();
};

self.onResize = function () {
  let custom = self.ctx.custom;
  // 위젯 전체 크기 조절
  const originWidth = self.ctx.settings.widget.widgetSize;
  let widgetFontSize = _.round((self.ctx.width / originWidth) * 10, 2);
  custom.$widget.css('font-size', `${widgetFontSize}px`);

  // Header와 Footer Height를 제외한 영역을 Main의 Height로 설정
  let headerHeight = custom.$widgetHeader.outerHeight(true);
  custom.$widgetContent.css('height', `calc(100% - ${headerHeight}px)`);
};

self.onEditModeChanged = function () {
  if (self.ctx.isEdit == true) {
    $('.add-btn-box', self.ctx.$container).css('display', 'none');
  } else {
    $('.add-btn-box', self.ctx.$container).css('display', 'flex');
  }
};

self.actionSources = function () {
  return {
    actionCustomButton: {
      name: 'Action Custom Button',
      multiple: false,
    },
    actionCellButton: {
      name: 'widget-action.action-cell-button',
      multiple: true,
    },
  };
};

self.typeParameters = function () {
  return {
    maxDatasources: -1, // 데이터 소스 1개로 제한
    maxDataKeys: -1, // 데이터 키 1개로 제한
    dataKeysOptional: false, // 데이터 키 1개 이상 필수
  };
};

function defineVariables() {
  let custom = (self.ctx.custom = {});
  let $container = self.ctx.$container;
  let settings = self.ctx.settings;
  let $scope = self.ctx.$scope;

  // Define Tags
  custom.$widget = $('#widget', $container);
  custom.$widgetHeader = $('.widget-header', $container);
  custom.$widgetTitle = $('.widget-title', $container);
  custom.$widgetAction = $('.widget-action', $container);
  custom.$widgetContent = $('.widget-content', $container);
  custom.$widgetFooter = $('.widget-footer', $container);

  custom.$thead = $('.thead', $container);
  custom.$tbody = $('.tbody', $container);

  // 초기 정렬값
  custom.sortColumn = 'name';
  custom.sortDec = false;
  custom.sort = false;

  // 업데이트 허용 parameter 기본값
  custom.update = true;

  custom.headList = [
    '구분',
    '토양 온도 ① (°C)',
    '토양 온도 ② (°C)',
    '토양 수분 ① (%)',
    '토양 수분 ② (%)',
    '토양 EC ① (μs/cm)',
    '토양 EC ② (μs/cm)',
  ];

  custom.keyList = ['name', 'temperature1', 'temperature2', 'humidity1', 'humidity2', 'ec1', 'ec2'];
}

// Create Widget Title
function setTitle() {
  self.ctx.custom.$widgetTitle.html(self.ctx.widget.config.title);
  self.ctx.custom.$widgetTitle.css(self.ctx.widget.config.titleStyle);
}

