  border-left: none;
}

header.widget-header .widget-header-right-section .tab-label.active {
  color: #1d6ef0;
  background-color: white;
}

/*
  Widget Content Area
*/

main.widget-content {
  width: 100%;
  border-bottom: 1px solid #dee0e5;
}

main.widget-content .card-box {
  margin-left: 2.4em;
}

main.widget-content .card-box .card-label-box {
  margin-bottom: 0.8em;
}

main.widget-content .card-box .card-label-box .line-hr {
