@import url('https://cdn.jsdelivr.net/gh/orioncactus/pretendard@v1.3.6/dist/web/static/pretendard.css');
@import url('https://fonts.googleapis.com/icon?family=Material+Icons|Material+Icons+Outlined');

#widget {
  width: 100%;
  height: 100%;
  font-size: 10px;
  font-family: 'Pretendard';

  border-left: 1px solid #dee0e5;
}

/*
  Widget Header Area
*/

header.widget-header {
  width: 100%;
  padding: 2em 3.2em;
  border-bottom: 1px solid #dee0e5;
}

header.widget-header .widget-header-left-section .widget-title {
  color: #1f2229;
  font-size: 1.8em;
  font-weight: 600;
}

header.widget-header .widget-header-right-section .tab-label {
  width: calc(11.6em / 1.4);
  font-size: 1.4em;
  padding: calc(0.8em / 1.4) 0;
  border: 1px solid #dee0e5;

  color: #999ea9;
  background-color: #f4f6f8;

  cursor: pointer;
}

header.widget-header .widget-header-right-section .tab-label:last-child {
  border-left: none;
}

header.widget-header .widget-header-right-section .tab-label.active {
  color: #1d6ef0;
  background-color: white;
}

/*
  Widget Content Area
*/

main.widget-content {
  width: 100%;
  border-bottom: 1px solid #dee0e5;

  padding: 1.6em 3.2em;
}

main.widget-content .card-box .line-hr {
  border: 2px solid black;
  width: 10px;
  height: 0;
  margin: 0;
}

main.widget-content .card-box .card-label {
  color: #5a616f;
  font-size: 1.4em;
  margin-left: calc(0.8em / 1.4);
  margin-right: calc(1.6em / 1.4);
}

/*
  Widget Content Area
*/

footer.widget-footer {
  width: 100%;
  background-color: #fafbfc;
}

footer.widget-footer .bed-container {
  padding-left: 3.2em;
  padding-top: 3.2em;
  padding-right: 3.2em;
}

footer.widget-footer .bed-container .label-container {
  margin-bottom: 0.8em;
}

footer.widget-footer .bed-container .label-container .left-section .bed-label {
  font-size: 2em;
  font-weight: 600;
  color: #1f2229;
}

footer.widget-footer .bed-container .label-container .left-section .material-icons {
  width: 1em;
  height: 1em;
  font-size: 2.4em;
  color: #b9bdc4;
  margin-right: calc(1.6em / 2.4);
}

footer.widget-footer .bed-container .label-container .label-text {
  font-size: 1.2em;
  color: #999ea9;
  font-weight: 500;
}

footer.widget-footer .bed-container .label-container .label-text b {
  color: #5a616f;
  font-weight: 600;
  margin-left: calc(0.4em / 1.2);
}

footer.widget-footer .bed-container .label-container .right-section .label-text {
  margin-left: calc(1.6em / 1.2);
}

footer.widget-footer .bed-container .label-container .right-section .material-icons-outlined {
  width: 1em;
  height: 1em;
  font-size: 1.6em;
  color: #6ba3ff;
  margin-left: calc(0.8em / 1.6);
}

footer.widget-footer .bed-container .heatmap-container {
  background-color: #f4f6f8;
  border: 1px solid #dee0e5;
  padding: 0.8em;
  gap: 0.2em;
}

footer.widget-footer .bed-container .heatmap-container .heatmap-box {
  cursor: pointer;
}

footer.widget-footer .bed-container .heatmap-container .heatmap-box:hover {
  box-shadow: 0 2px 2px 0 rgba(15, 17, 21, 0.3);
}

footer.widget-footer .bed-container .heatmap-container .heatmap-box .heatmap {
  width: 100%;
  height: 1.6em;
  background-color: #e55e63;
}
footer.widget-footer .bed-container .heatmap-container .heatmap-box .pos-text {
  width: 100%;
  height: calc(3.2em / 1.2);
  font-size: 1.2em;
  color: #999ea9;
  border: solid 1px #dee0e5;

  background-color: white;
}

footer.widget-footer .bed-container .heatmap-container .heatmap-box:hover .pos-text {
  color: #1f2229;
}

descriptor.controllerScript:
self.onInit = async function () {
  defineVariables();
  setTitle();
  insertHeaderAction();
  createTooltip();
  getDashboardParameter();

  makeTooltip();

  // await getLineData();
  // parseData();

  self.ctx.$scope.detailView = function (bed, position) {
    let actionDescriptor = self.ctx.actionsApi.getActionDescriptors('detailView');
    self.ctx.actionsApi.handleWidgetAction('', actionDescriptor[0], '', '', { bed, position }, '');
  };

  self.onResize();
};

self.onDestroy = function () {};

self.onDataUpdated = function () {
  if (self.ctx.datasources[0].type != 'function') {
    parseData();
    insertData();
  }
};

self.onResize = function () {
  let custom = self.ctx.custom;
  // 위젯 전체 크기 조절
  const originWidth = self.ctx.settings.widget.originWidth;
  let widgetFontSize = _.round((self.ctx.width / originWidth) * 10, 2);
  custom.$widget.css('font-size', `${widgetFontSize}px`);
};

self.actionSources = function () {
  return {
    widgetHeaderButton: {
      name: 'Custom Header Button',
      multiple: true,
    },
    detailView: {
      name: 'Detail View',
      multiple: true,
    },
  };
};

self.typeParameters = function () {
  return {
    maxDatasources: -1,
    maxDataKeys: -1,
    dataKeysOptional: true,
  };
};

// Define Variables
function defineVariables() {
  let custom = (self.ctx.custom = {});
  let $container = self.ctx.$container;

  // Define Tags
  custom.$widget = $('#widget', $container);
  custom.$widgetHeader = $('.widget-header', $container);
  custom.$widgetTitle = $('.widget-title', $container);
  custom.$widgetAction = $('.widget-action', $container);
  custom.$widgetContent = $('.widget-content', $container);
  custom.$widgetFooter = $('.widget-footer', $container);
  custom.$chart = $('.chart', $container);

  // Define Normal Variables
  let now = moment().valueOf();
  custom.startTs = moment(now).startOf('date').valueOf();
  custom.endTs = moment(now).endOf('date').valueOf();
}

// Create Widget Title
function setTitle() {
  self.ctx.custom.$widgetTitle.html(self.ctx.widget.config.title);
}

// Create Widget Header Action
function insertHeaderAction() {
  let actionDescriptor = self.ctx.actionsApi.getActionDescriptors('widgetHeaderButton');
  for (let i in actionDescriptor) {
    let $headerAction = $(`<i class='material-icons widget-header-action'>${actionDescriptor[i].icon}</i>`);

    $headerAction.on('click', e => {
      self.ctx.actionsApi.handleWidgetAction(
        e,
        actionDescriptor[i],
        self.ctx.datasources[0].entity.id,
        self.ctx.datasources[0].entityName,
        {
          selectedEntity: {
            entityId: self.ctx.datasources[0].entity.id,
            entityName: self.ctx.datasources[0].entityName,
            entityLabel: self.ctx.datasources[0].entityLabel,
          },
          startTs: self.ctx.custom.startTs,
          endTs: self.ctx.custom.endTs,
        },
        self.ctx.datasources[0].entityLabel
      );
    });
    self.ctx.custom.$widgetAction.append($headerAction);
  }
}

function createTooltip() {
  let custom = self.ctx.custom;
  custom.$tooltip = $(`<div></div>`);
  custom.$tooltip.css({
    display: 'none',
    position: 'absolute',
    zIndex: '1',
    fontSize: '1em',
    backgroundColor: '#2a2f33',
    color: 'white',
    border: 'solid 1px black',
    padding: '12px 16px',
    pointerEvents: 'none',
    width: '205px',
  });

  $('body').append(custom.$tooltip);
}

// Extract Dashboard's Parameters
function getDashboardParameter() {
  if (self.ctx.datasources[0].type === 'function') return {};
  let custom = self.ctx.custom;
  custom.dashboardParams = self.ctx.stateController.getStateParams();
  if (!_.isNil(custom.dashboardParams)) {
    if (!_.isNil(custom.dashboardParams.startTs)) {
      custom.startTs = custom.dashboardParams.startTs;
    }
    if (!_.isNil(custom.dashboardParams.endTs)) {
      custom.endTs = custom.dashboardParams.endTs;
    }
  }
}

function makeTooltip() {
  $('.bed-container .right-section .material-icons-outlined', self.ctx.$container).each(function (i, obj) {
    addTooltip($(this), '단계별 클러스터 수');
  });
}

function addTooltip($target, label) {
  let $content = $(`
    <div style="display: flex; align-items: center; justify-content: space-between; padding: 9px 12px">
      <material-icons class="material-icons-outlined" style="width: 16px; height: 16px; font-size: 16px; color: #b9bdc4; margin-right: 10px;">info</material-icons>
      <div style="font-size: 12px; color: white">${label}</div>
    </div>`);

  $content.css({
    // width: '160px',
    // height: '32px',
    backgroundColor: '#2a2f33',
    // padding: `16px`,
  });

  $target.tooltipster({
    content: $content,
    interactive: true,
    theme: 'tooltipster-transparent',
    trigger: 'hover',
    delay: 0,
  });
}

function calculateFormat(d, xDomain) {
  return d;
  // if (self.ctx.datasources[0].type === 'function') return d;

  const INTERVALS = [60 * 60 * 1000, 24 * 60 * 60 * 1000, 31 * 24 * 60 * 60 * 1000];

  // Set -> Array
  let target = Array.from(xDomain);

  let diff = target[target.length - 1] - target[0];

  if (diff > INTERVALS[2]) return moment(d).format('M월');
  if (diff > INTERVALS[1]) return moment(d).format('MM/DD');
  if (diff > INTERVALS[0]) return moment(d).format('HH:mm');
  if (diff < INTERVALS[0]) return moment(d).format('mm:ss');
}

